const canvas = document.getElementById('canvas1');
const ctx = canvas.getContext('2d');
ctx.canvas.width = window.innerWidth;
ctx.canvas.height = window.innerHeight;
//global variables
let particleArray = [];
let mouse = { //mouse position
    x: null,
    y: null,
    rad: 100
};

//events
window.addEventListener('mousemove',
    function(event) {
        mouse.y = event.y;
        mouse.x = event.x;
    }
);

window.addEventListener('mouseout',
    function() {
        mouse.y = undefined;
        mouse.x = undefined;
    }
);

//actual drawing code
function drawImage() {
    let imageWidth = png.width;
    let imageHeight = png.height;
    const data = ctx.getImageData(0, 0, imageWidth, imageHeight);
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    //class for indevidual particles
    class Particle {
        constructor(x, y, colour, size) {
            this.x = x + canvas.width / 2 - png.width * 2;
            this.y = y + canvas.height / 2 - png.height * 2;
            this.colour = colour;
            this.size = 2;
            this.basex = x + canvas.width / 2 - png.width * 2;
            this.basey = y + canvas.height / 2 - png.height * 2;
            this.dencity = (Math.random() * 10) + 2;
        }
        draw() {
            ctx.beginPath();
            ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2, true);
            ctx.closePath();
            ctx.fill();
        }
        update() {
            ctx.fillStyle = this.colour;

            //collision detection
            let dx = mouse.x - this.x;
            let dy = mouse.y - this.y;
            let dist = Math.sqrt(dx * dx + dy * dy);
            let fDX = dx / dist; //Force Direction X
            let fDY = dy / dist; //Force Direction Y

            // max distance, past that the force will be 0
            const maxDist = 100;
            let force = (maxDist - dist) / maxDist;
            if (force < 0) { force = 0; }

            let dirX = (fDX * force * this.dencity * 0.6);
            let dirY = (fDY * force * this.dencity * 0.6);

            if (dist < mouse.rad + this.size) {
                this.x -= dirX;
                this.y -= dirY;
            } else {
                if (this.x != this.basex) {
                    let dx = this.x - this.basex;
                    this.x -= dx / 20;
                }
                if (this.y != this.basey) {
                    let dy = this.y - this.basey;
                    this.y -= dy / 20;
                }
            }
            this.draw();
        }
    }

    function init() {
        particleArray = [];

        for (let y = 0, y2 = data.height; y < y2; y++) {
            for (let x = 0, x2 = data.width; x < x2; x++) {
                if (data.data[(y * 4 * data.width) + (x * 4) + 3] > 128) {
                    let posX = x;
                    let posY = y;
                    let colour = "rgb(" + data.data[(y * 4 * data.width) + (x * 4)] + "," +
                        data.data[(y * 4 * data.width) + (x * 4) + 1] + "," +
                        data.data[(y * 4 * data.width) + (x * 4 + 2)] + ")";
                    particleArray.push(new Particle(posX * 4, posY * 4, colour));
                }
            }
        }
    }

    function animate() {
        requestAnimationFrame(animate);
        ctx.fillStyle = "rgba(0,0,0,.5)";
        ctx.fillRect(0, 0, innerWidth, innerHeight);

        for (let I = 0; I < particleArray.length; I++) {
            particleArray[I].update();
        }
    }
    window.addEventListener('resize',
        function() {
            canvas.width = window.innerWidth;
            canvas.height = window.innerHeight;
            init();
        }
    );
    init();
    animate();
}
const png = new Image();
png.src = "data:image/png;base64,";

window.addEventListener('load', (event) => {
    console.log('page has loaded');
    ctx.drawImage(png, 0, 0);
    drawImage(); // the function I created
})